package Java_1201;

public class Val_cal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b = 10;
		short s = 20;
		int i = 40;
		
		int sum = b+ s+ i;
		int b_2 = i- s- b;
		
		System.out.println("더한 값:"+sum);
		System.out.println(b_2);
		System.out.println("유니코드 입력:"+'뺀'+sum);

	}

}
